import { Avatar } from "@mui/material";
import React, { useEffect, useState } from "react";
import db from "./firebase";
import "./SidebarChat.css";

const SidebarChat = ({ addNewChat, roomName, id }) => {
  const [seed, setSeed] = useState();
  console.log(id);
  const CreateChat = () => {
    const RoomName = prompt("Pleasse Enter Room name:");
    if (RoomName) {
      // Push all data into databse

      db.collection("rooms").add({
        RoomName: RoomName,
      });
    }
  };
  useEffect(() => {
    setSeed(Math.floor(Math.random() * 500));
  }, []);
  return !addNewChat ? (
    <div className="sidebarChat">
      <Avatar src={`https://avatars.dicebear.com/api/open-peeps/${seed}.svg`} />
      <div className="sidebarChat_info">
        <h2>{roomName}</h2>
        <p>Last message..</p>
      </div>
    </div>
  ) : (
    <div onClick={CreateChat} className="sidebarChat addnewchat">
      <h2>add new chat</h2>
    </div>
  );
};

export default SidebarChat;
